"main";let fs;_002‍.w("fs",[["default",["fs"],function(v){fs=v}]]);let connectDb,clearDatabase,createDbNode,connectDbNodes;_002‍.w("./database.js",[["connectDb",["connectDb"],function(v){connectDb=v}],["clearDatabase",["clearDatabase"],function(v){clearDatabase=v}],["createDbNode",["createDbNode"],function(v){createDbNode=v}],["connectDbNodes",["connectDbNodes"],function(v){connectDbNodes=v}]]);



async function setupDb(){
    const db = connectDb()
    await clearDatabase(db)
    
    const collectionsToCreate = [
            'market',
            'currency',
            'exchange',
            ]
    for (let collection of collectionsToCreate){
        await db.collection(collection).create()
    }
    
    const edgeCollectionsToCreate = [
        'marketHasExchange',
        'marketHasBaseCurrency',
        'marketHasQuoteCurrency',
        'exchangeHasCurrency',
    ]
    
    for (let edgeCollection of edgeCollectionsToCreate){
        await db.edgeCollection(edgeCollection).create()
    }
    
    
    
    // exchanges dir is read to load js files
    fs.readdir('exchanges', async (err,moduleFiles)=>{
        
        // modules are loaded in a object
        let exchangeModules = {}
        for (const moduleFile of moduleFiles){
            const exchangeName = moduleFile.split('.')[0]
            exchangeModules[exchangeName] = require(`./exchanges/${moduleFile}`)
            break
        }
        
        // then markets, currencies and exchanges documentsare create
        for (let exchangeModuleName of Object.keys(exchangeModules)){
            const module = exchangeModules[exchangeModuleName][exchangeModuleName]
            const markets = await module.fetchMarkets()
            
            let marketNodes = {}
            let currencyNodes = {}
            let exchangeNodes = {}
            
            let edgeTree = TempEdgesTree()
            
            for (let market of markets){
                
                const marketLabel = `${market.exchange} - ${market.baseCurrency}/${market.quoteCurrency}`
                
                if (!Object.keys(marketNodes).includes(marketLabel)){
                    marketNodes[marketLabel] = await createDbNode(db, 'market', {label: marketLabel})
                }
                
                if (!Object.keys(currencyNodes).includes(market.baseCurrency)){
                    currencyNodes[market] = await createDbNode(db, 'currency', {label: market.baseCurrency})
                }
                
                if (!Object.keys(currencyNodes).includes(market.quoteCurrency)){
                    currencyNodes[market.quoteCurrency] = await createDbNode(db, 'currency', {label: market.quoteCurrency})
                }
                
                if (!Object.keys(exchangeNodes).includes(market.exchange)){
                    exchangeNodes[market.exchange] = await createDbNode(db, 'exchange', {label: market.exchange})
                }
                
                
                // get node ids then generate links
                const marketNode = marketNodes[marketLabel]
                const exchange = exchangeNodes[marketLabel]
                const baseCurrency = currencyNodes[market.baseCurrency]
                const quoteCurrency= currencyNodes[market.quoteCurrency]
                
                edgeTree.add('marketHasExchange', marketNode, exchange)
                edgeTree.add('marketHasBaseCurrency', marketNode, baseCurrency)
                edgeTree.add('marketHasQuoteCurrency', marketNode, quoteCurrency)
                edgeTree.add('exchangeHasCurrency', exchange, baseCurrency)
                edgeTree.add('exchangeHasCurrency', exchange, quoteCurrency)
                
            }
            
            edgeTree.build()
        }
    })
}
function TempEdgesTree(){
    let edgeTree = {}
    return {
        add(type, from, to){
            if (!Object.keys(edgeTree).includes(type)){
                edgeTree[type] = {}
            }
            if (!Object.keys(edgeTree).includes(from)){
                edgeTree[type][from] = [to]
            }
            else {
                edgeTree[type][from].push(to)
            }
        },
        build(){
            for (let type of Object.keys(edgeTree)){
                for (let from of Object.keys(edgeTree[type])){
                    for (let to of edgeTree[type][from]){
                        connectDbNodes(type, from, to)
                    }
                }
            }
        }
    }
}




setupDb()
